```{.r .cell-code}
# Create a data frame with your publications
publications <- tibble(
  year = c(2023, 2022),
  title = c("NK cells from COVID-19 positive patients exhibit enhanced cytotoxic activity upon NKG2A and KIR2DL1 blockade.", "Sequential Single-Cell Transcriptional and Protein Marker Profiling Reveals TIGIT as a Marker of CD19 CAR-T Cell Dysfunction in Patients with Non-Hodgkin Lymphoma."),
  authors = c("Grace Lee, Robert Schauner, Juanita Burke, Jade Borocz, Smitha Vasana, Lukasz Sobieraj, Maria Girauodo, Zachary Jackson, Qasim Ansari, Maria Navas, Hany Sakr, and David Wald", "Zachary Jackson, Changjin Hong, Robert Schauner, Boro Dropulic, Paolo F. Caimi, Marcos de Lima, Maria Florencia Girauodo, Kalpana Gupta, Jane S. Reese, Tae Hyun Hwang, and David N. Wald"),
  journal = c("Frontiers in Immunology", "Cancer Discovery")
)

# Sort publications by year
publications <- publications %>%
  arrange(desc(year))

# Render each publication
for (i in unique(publications$year)) {
  cat("\n\n### ", i, "\n\n")

  publications_year <- filter(publications, year == i)

  for (j in 1:nrow(publications_year)) {
    cat("<div class='publication'>\n")
    cat("<div class='publication-title'>", publications_year$title[j], "</div>\n")
    cat("<div class='publication-authors'>", publications_year$authors[j], "</div>\n")
    cat("<div class='publication-journal'>", publications_year$journal[j], "</div>\n")
    cat("</div>\n\n")
  }
}
```

